		// 		if(a.status > b.status) return -sort.value;
		// 		else if (a.status < b.status) return sort.value;
		// 		else return 0;
		// 	})
		// }

		return (
			<div className="container">
				<div className="text-center">
					<h1>Manager Work</h1>
				</div>
				<div className="row">
					<div className={ isDisplayForm ? 'col-xs-4 col-sm-4 col-md-4 col-lg-4' : ''}>
						<TaskForm />
					</div>
					{/* Add work + Search Sort */}
					<div className={ isDisplayForm ? 'col-xs-8 col-sm-8 col-md-8 col-lg-8' : 'col-xs-12 col-sm-12 col-md-12 col-lg-12' }>
						<button type="button" className="btn btn-primary" onClick={this.props.onToggleForm}>
							<span className="fa fa-plus mr-5"></span>Add work
						</button>
						{/* Search Sort */}
						<Control onSearch={this.onSearch} onSort={this.onSort} sort={sort} />
						{/* List */}
						<div className="row mt-15">
							<div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
								<TaskList onFilter={this.onFilter} />
							</div>
						</div>
					</div>
				</div>
			</div>
		);
	}
}

var mapStateToProps = state => {
	return {
		isDisplayForm: state.isDisplayForm
	}
}

var mapDispatchToProps = (dispatch, props) => {
	return {
		onToggleForm: () => {
			dispatch(actions.toggleForm());
		}
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(App);
