import React, {Component} from 'react';
import './App.css';
import TaskForm from './components/TaskForm'
import Control from './components/Control'
import TaskList from './components/TaskList'
import _ from 'lodash'

class App extends Component {
	constructor(props){
		super(props);
		this.state = {
			isDisplayForm: false,
			taskEditting: null,
			filter: {
				name: '',
				status: -1
			},
			keyword: '',
			sort: {
				by: 'name',
				value: 1
			}
		}
		this.onToggleForm = this.onToggleForm.bind(this);
		this.onCloseForm = this.onCloseForm.bind(this);
		this.onAddWork = this.onAddWork.bind(this);
		this.onUpdateStatus = this.onUpdateStatus.bind(this);
		this.findIndex = this.findIndex.bind(this);
		this.onDelete = this.onDelete.bind(this);
		this.onUpdate = this.onUpdate.bind(this);
		this.onFilter = this.onFilter.bind(this);
		this.onSearch = this.onSearch.bind(this);
		this.onSort = this.onSort.bind(this);
	}
	s4(){
		return Math.floor((1+Math.random()) * 0x10000).toString(16).substring(1);
	}
	generateID(){
		return this.s4() + '-' + this.s4() + '-' + this.s4() + '-' + this.s4(); 
	}
	onToggleForm(){
		if(this.state.taskEditting && this.state.isDisplayForm){
			this.setState({
				taskEditting: null
			})
		} else {
			this.setState({
				isDisplayForm: !this.state.isDisplayForm,
				taskEditting: null
			})
		}
		console.log(this.state.tasks);
	}
	onCloseForm(params){
		this.setState({
			isDisplayForm: params,
			taskEditting: null
		})
	}
	onAddWork(data){
		var { tasks } = this.state;

		if(data.id === ''){
			data.id = this.generateID();
			tasks.push(data);
		} else {
			var index = this.findIndex(data.id);
			tasks[index] = data;
		}

		this.setState({
			tasks: tasks,
			taskEditting: null
		});
		localStorage.setItem('tasks', JSON.stringify(tasks));
	}
	onUpdateStatus(id){
		var { tasks } = this.state;
		// var index = this.findIndex(id);
		var index = _.findIndex(tasks, (task) => {
			return task.id === id;
		})
		if(index !== -1){
			tasks[index].status = !tasks[index].status;

			this.setState({
				tasks: tasks
			});
			localStorage.setItem('tasks', JSON.stringify(tasks));
		}
	}
	findIndex(id){
		var { tasks } = this.state;
		var result = -1;

		tasks.forEach((task, index) => {
			if(task.id === id){
				result = index;
			}
		})
		return result;
	}
	onDelete(id){
		var { tasks } = this.state;
		var index = this.findIndex(id);
		if(index !== -1){
			tasks.splice(index, 1);

			this.setState({
				tasks: tasks
			});
			localStorage.setItem('tasks', JSON.stringify(tasks));
		}
		this.onCloseForm();
	}
	onUpdate(id){
		var { tasks } = this.state;
		var index = this.findIndex(id);
		var taskEditting = tasks[index];

		this.setState({
			taskEditting: taskEditting,
			isDisplayForm: true
		})
	}
	onFilter(filterName, filterStatus){
		filterStatus = parseInt(filterStatus, 10);
		this.setState({
			filter: {
				name: filterName.toLowerCase(),
				status: filterStatus
			}
		})
	}
	onSearch(keyword){
		this.setState({
			keyword: keyword.toLowerCase()
		})
	}
	onSort(sortBy, sortValue){
		this.setState({
			sort: {
				by: sortBy,
				value: sortValue
			}
		})
	}

	render(){
		var { isDisplayForm, taskEditting, filter, keyword, sort } = this.state;
		// if(filter){
		// 	if(filter.name){
		// 		tasks = tasks.filter((task) => {
		// 			return task.name.toLowerCase().indexOf(filter.name) !== -1;
		// 		})
		// 	}

		// 	tasks = tasks.filter((task) => {
		// 		if(filter.status === -1){
		// 			return tasks;
		// 		} else {
		// 			return task.status === (filter.status === 1 ? true : false);
		// 		}
		// 	})
		// }
		// if(keyword){
		// 	tasks = tasks.filter((task) => {
		// 		return task.name.toLowerCase().indexOf(keyword) !== -1;
		// 	})
		// }
		// if(sort.by === 'name'){
		// 	tasks.sort((a, b) => {
		// 		var nameA = a.name.toLowerCase();
		// 		var nameB = b.name.toLowerCase();
		// 		if(nameA > nameB) return sort.value;
		// 		else if (nameA < nameB) return -sort.value;
		// 		else return 0;
		// 	})
		// } else {
		// 	tasks.sort((a, b) => {
		// 		if(a.status > b.status) return -sort.value;
		// 		else if (a.status < b.status) return sort.value;
		// 		else return 0;
		// 	})
		// }
		var elmTaskForm = isDisplayForm ? <TaskForm taskEditting={taskEditting} onCloseForm={this.onCloseForm} onAddWork={this.onAddWork} /> : '';

		return (
			<div className="container">
				<div className="text-center">
					<h1>Manager Work</h1>
				</div>
				<div className="row">
					<div className={ isDisplayForm ? 'col-xs-4 col-sm-4 col-md-4 col-lg-4' : ''}>
						{elmTaskForm}
					</div>
					{/* Add work + Search Sort */}
					<div className={ isDisplayForm ? 'col-xs-8 col-sm-8 col-md-8 col-lg-8' : 'col-xs-12 col-sm-12 col-md-12 col-lg-12' }>
						<button type="button" className="btn btn-primary" onClick={this.onToggleForm}>
							<span className="fa fa-plus mr-5"></span>Add work
						</button>
						{/* Search Sort */}
						<Control onSearch={this.onSearch} onSort={this.onSort} sort={sort} />
						{/* List */}
						<div className="row mt-15">
							<div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
								<TaskList onUpdateStatus={this.onUpdateStatus} onDelete={this.onDelete} onUpdate={this.onUpdate} onFilter={this.onFilter} />
							</div>
						</div>
					</div>
				</div>
			</div>
		);
	}
}

export default App;
