import React, {Component} from 'react';
import TaskItem from './TaskItem'
import { connect } from 'react-redux';

class TaskList extends Component {
    constructor(props){
        super(props);

        this.state = {
            filterName: '',
            filterStatus: -1
        }
    }
    onChange = (event) => {
        var target = event.target;
        var name = target.name;
        var value = target.value;

        this.props.onFilter(
            name === 'filterName' ? value : this.state.filterName,
            name === 'filterStatus' ? value : this.state.filterStatus
        )

        this.setState({
            [name]: value
        });
    }

	render(){
        var { tasks, keyword } = this.props;
        if(keyword){
            tasks = tasks.filter((task) => {
                return task.name.toLowerCase().indexOf(keyword) !== -1;
            })
        }
        var elmTasks = tasks.map((task, index) => {
            return <TaskItem key={task.id} task={task} index={index} onUpdate={this.props.onUpdate} />
        })
        var { filterName, filterStatus } = this.state;

		return (
			<table className="table table-bordered table-hover">
                <thead>
                    <tr>
                        <th className="text-center">STT</th>
                        <th className="text-center">Name</th>
                        <th className="text-center">Status</th>
                        <th className="text-center">Action</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td></td>
                        <td>
                            <input type="text" name="filterName" className="form-control" value={filterName} onChange={this.onChange} />
                        </td>
                        <td>
                            <select name="filterStatus" className="form-control" value={filterStatus} onChange={this.onChange}>
                                <option value={-1}>All</option>
                                <option value={0}>Hide</option>
                                <option value={1}>Active</option>
                            </select>
                        </td>
                        <td></td>
                    </tr>
                    {elmTasks}
                </tbody>
            </table>
		);
	}
}

const mapStateToProps = (state) => {
    return{
        tasks: state.tasks,
        keyword: state.keyword
    }
}

export default connect(mapStateToProps, null)(TaskList);
